var ComponentsEditors = function() {
	var t = function() {
		jQuery().wysihtml5
				&& $(".wysihtml5").size() > 0
				&& $(".wysihtml5")
						.wysihtml5(
								{
									stylesheets : [ "../assets/css/wysiwyg-color.css" ]
								})
	}, s = function() {
		$(".summernote_1").summernote({
			height : 200
		}), $(".summernote_2").summernote({
			height : 200
		})
	};
	return {
		init : function() {
			t(), s()
		}
	}
}();
jQuery(document).ready(function() {
	ComponentsEditors.init()
});